    public void dbInsert();    /**     * Demande d'une suppression ou d'une annulation physique au serveur     */    public void dbDelete();    /**     * Affichage en mode Lecture     */    public void displayReadMode();    /**     * Affichage en mode Insertion     */    public void displayInsertMode();    /**     * Affichage en mode Modification     */    public void displayUpdateMode();    /**     * Affichage en Mode disable     */    public void displayDisableMode();    /**     * Méthode pour l'update de tous les champs     */    public void updateAllFields();    public void updateAllFields(Object donnee);    public void setEnabled(boolean enabled);    public void setRequestFocusEnabled(boolean enabled);    //public void setLibellé(String libellé);}