   try {            modelpayement = new srcastra.astra.gui.sys.tableModel.dossierTableModel.DossierPayementTableModel(parent.getServeur(), parent.getCurrentUser(), parent, (AbstractBuffer) parent.getServeur().workWithDecision(null, parent.getCurrentUser().getUrcleunik(), ActionToolBar.ACT_READ, null, 0, 0, 0, AbstractRequete.DIVERS));            ArrayList typepayement = parent.getServeur().renvTypePayementRmiObject(parent.getCurrentUser().getUrcleunik()).getList2(parent.getCurrentUser().getUrcleunik(), parent.getCurrentUser().getUrlmcleunik());            modelpayement.setTypepaiement(typepayement);        } catch (srcastra.astra.sys.rmi.Exception.ServeurSqlFailure se) {            ErreurScreenLibrary.displayErreur(this, ErreurScreenLibrary.SERVEUR_SQL_FAILURE, srcastra.astra.Astra.DEBUG, se);        }        catch (java.rmi.RemoteException re) {            ErreurScreenLibrary.displayErreur(this, ErreurScreenLibrary.REMOTE_EXCEPTION, srcastra.astra.Astra.DEBUG, re);        }        //grp_Table_payement.setModel(modelpayement);        modelpayement.retrieveData();        grp_Table_payement.setAutoCreateColumnsFromModel(false);        grp_Table_payement.setModel(modelpayement);        ColumnData[] col = modelpayement.loadColumnNames();        for (int k = 0; k < modelpayement.loadColumnNames().length; k++) {            DefaultTableCellRenderer renderer = new srcastra.astra.gui.sys.tableModel.dossierTableModel.ColoredTableCellRenderer();            renderer.setHorizontalAlignment(col[k].getM_alignment());            TableColumn column;            column = new TableColumn(k, col[k].getM_width(), renderer, null);            grp_Table_payement.addColumn(column);        }        JTableHeader header = grp_Table_payement.getTableHeader();        header.setUpdateTableInRealTime(false);        if (grp_Table_payement.getRowCount() > 0) {            grp_Table_payement.changeSelection(0, 0, false, false);            setUser();            // setListSelector();        }    }    private void setDocuments() {        grp_Formated_payement.getGrp_JText_encode().setDocument(new srcastra.astra.gui.components.textFields.mask.Decimal(grp_Formated_payement.getGrp_JText_encode()));        grp_TField_libelle.setDocument(new DefaultMask(0, 50, parent.getCurrentUser().getLangage(), DefaultMask.CASE_UNSENSITIVE));    }    private void initComponents() {//GEN-BEGIN:initComponents        java.awt.GridBagConstraints gridBagConstraints;        jPanel1 = new javax.swing.JPanel();        jLabel1 = new javax.swing.JLabel();        grp_Formated_payement = new srcastra.astra.gui.components.textFields.ATextField();        jLabel2 = new javax.swing.JLabel();        grp_Adate_payement = new srcastra.astra.gui.components.date.thedate.ADate();        jLabel3 = new javax.swing.JLabel();        grp_TField_invisible = new javax.swing.JTextField();        grp_ACB_TypePayement = new srcastra.astra.gui.components.combobox.liste.Liste();        jLabel12 = new javax.swing.JLabel();        grp_TField_libelle = new srcastra.astra.gui.components.textFields.ATextField();        jRadioButton1 = new javax.swing.JRadioButton();        jPanel2 = new javax.swing.JPanel();        jPanel3 = new javax.swing.JPanel();        jPanel4 = new javax.swing.JPanel();        jPanel5 = new javax.swing.JPanel();        jScrollPane1 = new javax.swing.JScrollPane();        grp_Table_payement = new javax.swing.JTable();        jPanel6 = new javax.swing.JPanel();        jPanel10 = new javax.swing.JPanel();        jPanel12 = new javax.swing.JPanel();        jPanel11 = new javax.swing.JPanel();        grp_Label_Unom = new javax.swing.JLabel();        grp_Label_Unom2 = new javax.swing.JLabel();        grp_Label_Uprenom = new javax.swing.JLabel();        grp_Label_Uprenom2 = new javax.swing.JLabel();        grp_Label_succursale = new javax.swing.JLabel();        grp_Label_succursale2 = new javax.swing.JLabel();        jPanel9 = new javax.swing