            parent.getServeur().renvDossierRmiObject(parent.getCurrentUser().getUrcleunik()).setDateNCService(parent.getCurrentUser().getUrcleunik(), d, parent.getDossier().getDrcleunik(), numfact, parent.getCurrentUser().getUreecleunik(), true);                    parent.getDossier().setNumfact(numfact);                    parent.getDossier().setDr_facture(1);                    //  parent.getServeur().renvParamCompta(parent.getCurrentUser().getUrcleunik()).commitNumpiece(parent.getCurrentUser().getUrcleunik());                }                dateFacturation.dispose();            }            if (parent.getDossier().getDr_facture() == 1)                new srcastra.astra.gui.modules.printing.PrintingPreview2(parent.getSuperOwner(), parent.getServeurDossier(), parent.getDossier(), parent.getCurrentUser(), parent.getMainframe().getMediator(), parent.getServeur(), 1, true);        // Add your handling code here:        } catch (srcastra.astra.sys.rmi.Exception.ServeurSqlFailure se) {            ErreurScreenLibrary.displayErreur(this, ErreurScreenLibrary.SERVEUR_SQL_FAILURE, srcastra.astra.Astra.DEBUG, se);        }        catch (java.rmi.RemoteException re) {            ErreurScreenLibrary.displayErreur(this, ErreurScreenLibrary.REMOTE_EXCEPTION, srcastra.astra.Astra.DEBUG, re);        }        //new FacturePrompt(parent.getSuperOwner(),true,parent).show();    }    // Variables declaration - do not modify//GEN-BEGIN:variables    private srcastra.astra.gui.components.combobox.liste.Liste grp_ACB_TypePayement;    private srcastra.astra.gui.components.date.thedate.ADate grp_Adate_payement;    private srcastra.astra.gui.components.textFields.ATextField grp_Formated_payement;    private javax.swing.JLabel grp_Label_Unom;    private javax.swing.JLabel grp_Label_Unom2;    private javax.swing.JLabel grp_Label_Uprenom;    private javax.swing.JLabel grp_Label_Uprenom2;    private javax.swing.JLabel grp_Label_succursale;    private javax.swing.J