        hot.setHl_email(grp_TField_AdresseMail.getText());        hot.setHl_adresse(grp_TField_Adresse.getText());        // hot.setHl_cp_libele(grp_LSelect_cp.getText());        // hot.setHl_cp_libele(grp_LSelect_cp.getText());        hot.setHl_localite(grp_TField_localite.getText());        hot.setHl_pays_cleUnik(grp_LSelect_Pays.getCleUnik());        hot.setHl_pays_libele(grp_LSelect_Pays.getText());        hot.setHl_cccf(grp_TField_CCCF.getText());        hot.setStatut(grp_ACB_Statut.getSelectedCleUnik());//        hot.setHl_valeur(Float.parseFloat(grp_TField_Valeur.getText()));        hot.setHl_vouch((grp_CBox_Voucher.isSelected()) ? 1 : 0);        // hot.setHl_langue_cleUnik(grp_LSelect_Langue.getCleUnik());        hot.setHl_dateEmission(grp_ADate_DateEmission.getDate());        hot.setHl_prest_nom(grp_TField_Prestataire_Nom.getText());        hot.setHl_prest_adresse(grp_TField_Prestataire_Adresse.getText());        //hot.setHl_prest_cp_cleUnik(grp_LSelect_Prestataire_cp.getCleUnik());        hot.setHl_prest_cp_libele(grp_LSelect_Prestataire_cp.getText());        hot.setHl_prest_localite(grp_TField_Prestataire_Localite.getText());        hot.setHl_prest_pays_cleUnik(grp_LSelect_Prestataire_Pays.getCleUnik());        hot.setHl_prest_pays_libele(grp_LSelect_Prestataire_Pays.getText());        hot.setHl_prest_email(grp_TField_Prestataire_AdresseMail.getText());        hot.setHl_memo(grp_TArea_memo.getText());        parent.getHotel().prepareAffichage();        return hot;    }    /**     * Demande de sélection au serveur     */    public void dbSelect() {    }    /**     * Demande de sélection en vue d'une modification au serveur     */    public void dbSelectForUpdate() {    }    /**     * Demande d'une modification au serveur     */    public void dbUpdate() {        chargeHotelClassUpdate();        parent.getHotel().setLocalyModify(true);        parent.calcu