    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
               try{
                String path=srcastra.astra.sys.ManageDirectory.testDirectory("config");
                File file2=new File(path+"\\server.dat");
                file2.createNewFile();
                ServerIp ip=new ServerIp();
                ip.setFireWall(grp_check_firewall.isSelected()?1:0);
                ip.setIp(grp_TField_ip.getText());
                ObjectOutputStream out=new ObjectOutputStream(new FileOutputStream(file2));
                out.writeObject(ip);
                out.close();     
                dispose();
               }catch(Exception e){
                   e.printStackTrace();
               }
    }//GEN-LAST:event_jButton1ActionPerformed
    
