package srcastra.astra.sys.reseaux;/** * @version 1.10 1997-06-27 * @author Cay Horstmann */import java.io.*;import java.net.*;import srcastra.astra.sys.classetransfert.Loginusers_T;public class SocketClient extends java.lang.Thread{     public SocketClient(Loginusers_T login){       m_login=login;      }    public void run(){      try      {           InetAddress ia=InetAddress.getByName("192.168.1.53");          Socket s = new Socket(ia,7800);          ObjectInputStream in=new ObjectInputStream(s.getInputStream());          ObjectOutputStream out=new ObjectOutputStream(s.getOutputStream());          ClientResaux_T cli=new ClientResaux_T(m_login.getUrcleunik());          out.writeObject(cli);          out.flush();         while (more)         {               Object line = in.readObject();            if (line == null) more = false;            else               System.out.println(line);         }      }      catch (IOException e)      {  System.out.println("Error" + e);      }      catch(ClassNotFoundException cn){       cn.printStackTrace();         }                            }        /** Getter for property more.     * @return Value of property more.     */    public boolean isMore() {        return more;    }        /** Setter for property more.     * @param more New value of property more.     */    public void setMore(boolean more) {        this.more = more;    }        Loginusers_T m_login;    boolean more=true;}